import React, { useState, useEffect } from 'react';

const ActualizarStock: React.FC = () => {
  const [id_producto, setIdProducto] = useState('');
  const [nombre_producto, setNombreProducto] = useState('');
  const [stock, setStockActual] = useState(0);
  const [nuevoStock, setNuevoStock] = useState(0);
  const [error, setError] = useState('');

  // Funci칩n para buscar el producto por ID o nombre
  const buscarProducto = async () => {
    try {
      const response = await fetch(`http://localhost:5000/productos/${id_producto}`);
      const data = await response.json();
      setNombreProducto(data.nombre_producto);
      setStockActual(data.stock);
      setError('');
    } catch (error) {
      console.error('Error al buscar el producto:', error);
      setError('producto no encontrado');
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (isNaN(nuevoStock)) {
      setError('El nuevo stock debe ser un nu패mero');
      return;
    }
    

    try {
      const response = await fetch(`http://localhost:5000/productos/${id_producto}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ stock: nuevoStock }),
      });

      if (response.ok) {
        alert('Stock actualizado exitosamente');
        // Puedes actualizar el estado de los productos en otros componentes si es necesario
      } else {
        alert('Error al actualizar el stock');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>ID del Producto:</label>
        <input
          type="text"
          value={id_producto}
          onChange={(e) => setIdProducto(e.target.value)}
          onBlur={buscarProducto} // Buscar el producto cuando se pierde el foco
        />
      </div>
      <div>
        <label>Nombre del Producto:</label>
        <input type="text" value={nombre_producto} readOnly />
      </div>
      <div>
        <label>Stock Actual:</label>
        <input type="number" value={stock} readOnly />
      </div>
      <div>
        <label>Nuevo Stock:</label>
        <input
          type="number"
          value={nuevoStock}
          onChange={(e) => setNuevoStock(Number(e.target.value))}
        />
      </div>
      <button type="submit">Actualizar Stock</button>
    </form>
  );
};

export default ActualizarStock;
