import React, { useState } from 'react';



function ActualizarStock() {
  const [idProducto, setIdProducto] = useState<string>('');
  const [nuevoStock, setNuevoStock] = useState<number>(0);
  const [error, setError] = useState<string | null>(null);

  const handleEditarStock = async () => {
    try {
      const response = await fetch(`/api/productos/${idProducto}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ stock: nuevoStock }),
      });

      if (!response.ok) {
        throw new Error('Error al actualizar el stock');
      }

      // Manejar la respuesta exitosa (opcional)
      console.log('Stock actualizado correctamente');
      // Mostrar un mensaje de éxito al usuario
    } catch (error: any) {
      console.error('Error:', error);
      setError(error.message);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Ingrese el ID del producto"
        value={idProducto}
        onChange={(e) => setIdProducto(e.target.value)}
      />
      <input
        type="number"
        placeholder="Ingrese el nuevo stock"
        value={nuevoStock}
        onChange={(e) => setNuevoStock(Number(e.target.value))}
      />
      <button onClick={handleEditarStock}>Editar Stock</button>
      {error && <p>{error}</p>}
    </div>
  );
}


export default ActualizarStock;
