import React, { useState } from 'react';

const ConsultarStock: React.FC = () => {
  const [idProducto, setIdProducto] = useState('');
  const [nombreProducto, setNombreProducto] = useState('');
  const [stock, setStock] = useState(null);
  const [error, setError] = useState<null | string>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/productos', {
        method: 'GET', // Cambia a GET para consultar
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        // Suponiendo que la respuesta del backend sea un objeto con el stock
        setStock(data.stock);
        setError(null);
      } else {
        setError('Error al consultar el producto');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Error inesperado');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="idProducto">ID Producto:</label>
      <input
        type="text"
        id="idProducto"
        value={idProducto}
        onChange={(e) => setIdProducto(e.target.value)}
      />

      <label htmlFor="nombreProducto">Nombre Producto:</label>
      <input
        type="text"
        id="nombreProducto"
        value={nombreProducto}
        onChange={(e) => setNombreProducto(e.target.value)}
      />

      <button type="submit">Consultar</button>

      {stock !== null && <p>Stock: {stock}</p>}
      {error && <p>{error}</p>}
    </form>
  );
};

export default ConsultarStock;
