import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Producto {
  id_producto: number;
  nombre_producto: string;
  stock: number;
}

const ConsultarStock: React.FC = () => {
  const [idProducto, setIdProducto] = useState<number | null>(null);
  const [nombreProducto, setNombreProducto] = useState<string>('');
  const [stock, setStock] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const consultarStock = async () => {
    setIsLoading(true);
    setError(null);

    try {
      let url = 'http://localhost:5000/productos'; // Replace with your backend URL
      if (idProducto) {
        url += `/${idProducto}`;
      } else if (nombreProducto) {
        url += `?nombre_producto=${nombreProducto}`; // Assuming backend accepts query params
      } else {
        throw new Error('Debe ingresar un ID o nombre de producto');
      }

      const response = await axios.get(url);
      const producto: Producto | Producto[] = response.data;

      if (Array.isArray(producto)) {
        // Handle multiple products (assuming backend returns an array for GET all)
        setError('Se encontraron varios productos. Por favor, refine su búsqueda.');
      } else {
        setStock(producto.stock);
      }
    } catch (error) {
      console.error(error);
      setError(error.message || 'Error al consultar stock');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    // Clear previous state on component mount or when search criteria change
    setStock(null);
    setError(null);
  }, [idProducto, nombreProducto]);

  return (
    <div>
      <h2>Consulta de Stock</h2>
      <div>
        <label htmlFor="idProducto">ID Producto: </label>
        <input
          type="number"
          id="idProducto"
          value={idProducto || ''}
          onChange={(e) => setIdProducto(Number(e.target.value) || null)}
        />
      </div>
      <div>
        <label htmlFor="nombreProducto">Nombre Producto: </label>
        <input
          type="text"
          id="nombreProducto"
          value={nombreProducto}
          onChange={(e) => setNombreProducto(e.target.value)}
        />
      </div>
      <button onClick={consultarStock} disabled={isLoading}>
        {isLoading ? 'Consultando...' : 'Consultar Stock'}
      </button>
      {error && <p className="error">{error}</p>}
      {stock !== null && (
        <p>
          Stock: <b>{stock}</b>
        </p>
      )}
    </div>
  );
};

export default ConsultarStock;
