import React, { useState, useEffect } from 'react';

const ConsultarStock: React.FC = () => {
  const [id_producto, setIdProducto] = useState('');
  const [nombre_producto, setNombreProducto] = useState('');
  const [stock, setStockActual] = useState(0);
  const [error, setError] = useState('');

  // Function to fetch product data by ID
  const buscarProducto = async () => {
    try {
      const response = await fetch(`http://localhost:5000/productos/${id_producto}`);

      if (!response.ok) {
        throw new Error('Error al buscar el producto');
      }

      const data = await response.json();
      setNombreProducto(data.nombre_producto);
      setStockActual(data.stock);
      setError('');
    } catch (error) {
      console.error('Error:', error);
      setError('Producto no encontrado');
    }
  };

  return (
    <form onSubmit={buscarProducto}>
      <div>
        <label>ID del Producto:</label>
        <input
          type="text"
          value={id_producto}
          onChange={(e) => setIdProducto(e.target.value)}
        />
      </div>
      <div>
        <label>Nombre del Producto:</label>
        <input type="text" value={nombre_producto} readOnly />
      </div>
      <div>
        <label>Stock Actual:</label>
        <input type="number" value={stock} readOnly />
      </div>
      <button type="submit">Consultar Stock</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default ConsultarStock;
